import React from 'react';
import Card from './Card.jsx';
import styles from './Cards.module.css'


export default function Cards({cities, onRemove}) {
  // acá va tu código
  // tip, podés usar un map

  return (
  <div className={styles.Cards} >
    
    {cities.map(city =>(
        <Card /* todo componente que lleve map, debe tener un key en su primer elemento */
        key={c.id}//key es reservada de react para su funcionamiento, no se ve. Pero si no esta no se acualiza el DOM del navegador
        max={c.max}//a esto tambien
        min={c.min}
        name={c.name}
        img={c.img}
        onClose={() => onRemove(c.id) }
      />
      ) )  
    }  
  
  </div>
 ) 

};
