import React from 'react';
import Card from './Card.jsx';
import styles from './Cards.module.css'


export default function Cards(props) {
  // acá va tu código
  // tip, podés usar un map

  return (
  <div className={styles.Cards} >
    
    {
      props.cities?.map(c =>(
        <Card
        max={c.main.temp_max}//a esto tambien
        key={c.id}//key es reservada de react para su funcionamiento, no se ve
        min={c.main.temp_min}
        name={c.name}
        img={c.weather[0].icon}
        onClose={() => (c.name) }
      />
      ) )  
    }  
    <div>
     {/*  <Remove/> */}
    </div>
  </div>
 ) 

};
function Temp({label, temp}){
  return (
    <div className={styles.temp}>
      <span>{label} </span>
      <span>{temp} </span>
    </div>
  )
}
 function Remove(){
  document.getElementById('close').onClose = function(){
    this.parentNode.parentNode.parentNode
    .removeChild(this.parentNode.parentNode);
    return false;
 }
}
